import React from 'react';
import {getHeaders} from './utils.js';

class LikeButton extends React.Component { 

    constructor(props) {
        super(props);

        this.toggleLike = this.toggleLike.bind(this);
        this.like = this.like.bind(this);
        this.unlike = this.unlike.bind(this);
        this.remakePost = this.props.remakePost.bind(this);
    }

    toggleLike (ev) {
        if(this.props.likeId) {
            this.unlike();
        } else {
            this.like();
        }
    }
    like () {
        const postId = this.props.postId;
        const url = `https://photo-app-secured.herokuapp.com/api/posts/${postId}/likes`
        fetch(url, {
            headers: getHeaders(),
            body: JSON.stringify({'postId': postId}),
            method: 'POST'
        })
        .then(response => response.json())
        .then(data => {
            this.props.remakePost();
        })
    }

    unlike () {
        // console.log("UNLIKE");
        const postId = this.props.postId;
        const likeId = this.props.likeId;
        const url =  `https://photo-app-secured.herokuapp.com/api/posts/${postId}/likes/${likeId}`
        fetch(url, {
            headers: getHeaders(),
            method: 'DELETE'
        })
        .then(response => response.json())
        .then(data => {
            this.props.remakePost()
        })
    }

    render () {
        const likeId = this.props.likeId;
        return (
            <button role="switch"
            className='like'
                aria-label="Like Button" 
                aria-checked={likeId ? true : false}
                onClick={this.toggleLike}>
                <i className={likeId ? 'fas fa-heart' : 'far fa-heart'}></i>
            </button>
        ); 
    }

}
export default LikeButton;
